package com.javabasics;

public class basicsloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 1;

		while(a <= 5)
		{
		    System.out.println(a);
		    a++;
		}
		
		int b= 1;

		do {
		    System.out.println(a);
		    b++;
		} while(b <= 5);


	for(int i=1;i<=4;i++)
	{
		System.out.println(i);
	}
    for(int i=1;i<=3;i++)
    {
    	
    	System.out.println("*");
    }
}
}
