package loops.starpatternss;

public class tringles {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 4;
		for(int row = 1; row <= n; row++)
		{
		    for(int star = 1; star <= row; star++)
		    {
		        System.out.print("*");
		    }
		    System.out.println();
		}

	}

}
